import React from "react";
import AddToDo from './addToDo'
import Row from './row';

var headers = {
    "Content-Type": "application/json",                                                                                                
    "Access-Control-Origin": "*"
   }

class TodosPage2 extends React.Component {

    state = {
        data:[]
    }
    constructor(props) {
        super(props);
        this.state = {
            data:[],
            textValue: ''
        }
    }

    componentDidMount() {
        fetch("https://todo-checkpoint-api.herokuapp.com/api/todos/mauricio", {
            method: "GET",
            headers: headers
        })
        .then(res=>res.json()).then(data=>{ 
            //add the data to component state
            this.setState({data});
        });
    }

    setText = (e) => {
        this.setState({
            text: e.target.value
        })
    }


    render() {
        //when data loads, your component will render the rows of the data using array.map()
        return (
            <div>
                <AddToDo textValue={this.state.textValue} setText={this.setText}/>
                <br></br>
                {this.state.data.map((row,i)=><Row text11={row.title} isChecked={row.checked}></Row>)}
            </div>
        )
    }
}

export default TodosPage2;
