import React from "react";
import TextField from '@material-ui/core/TextField';
import { makeStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';

const useStyles = makeStyles(theme => ({
    textField: {
        marginLeft: theme.spacing(1),
        marginRight: theme.spacing(1),
        width: 1000,
    },
}));

var headers = {
    "Content-Type": "application/json",
    "Access-Control-Origin": "*"
  }

function addNew(activity) {
    var data = {
        "title": activity,
        "checked": "false"
    }


    fetch("https://todo-checkpoint-api.herokuapp.com/api/todos/mauricio", {
        method: "POST",
        headers: headers,
        body: JSON.stringify(data)
    })
        .then(function (response) {
            return response.json();
        })
        .then(function (data) {
            console.log(data)
        });
}

export default function AddToDo() {


    const classes = useStyles();
    return (
        <div style={{
            display: "flex"
        }}>
            <TextField className={classes.textField} label="Add todo"></TextField>
            <Button onClick={addNew()} variant="contained" >Submit</Button>
        </div>
    )

}
